import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Typography, Grid, Paper, Box, AppBar, Toolbar, IconButton, Menu, MenuItem, CssBaseline, Button, Avatar, Drawer, List, ListItem, ListItemIcon, ListItemText } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { Menu as MenuIcon, Dashboard as DashboardIcon, ShoppingCart as ShoppingCartIcon, Settings as SettingsIcon, LocalShipping as LocalShippingIcon, People as PeopleIcon, ExitToApp as ExitToAppIcon, Calculate as CalculateIcon } from '@mui/icons-material';
import { Link, Route, Routes } from 'react-router-dom';
import FeeCalculatorPage from '../pages/FeeCalculator';
import Settings from '../pages/Settings';
import Logout from '../pages/Logout';
import UserDashboard from '../pages/UserDashboard';

const theme = createTheme({
    palette: {
        mode: 'dark',
        primary: {
            main: '#90caf9',
        },
        secondary: {
            main: '#f48fb1',
        },
        background: {
            default: '#1c1c1c',
            paper: '#2c2c2c',
        },
        text: {
            primary: '#ffffff',
        },
    },
});

function ProductCard() {
    const { productId } = useParams();
    const [product, setProduct] = useState(null);

    useEffect(() => {
        fetch(`http://localhost:8000/products/${productId}`, {
            credentials: 'include',
            headers: {
                'Authorization': 'Basic ' + btoa('juggy:Idus1234@@'),
            },
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => setProduct(data))
            .catch(error => console.error('Fetch error:', error));
    }, [productId]);

    if (!product) {
        return <div>Loading...</div>;
    }

    const drawerWidth = 240;

    return (
        <ThemeProvider theme={theme}>
            <CssBaseline />
            <AppBar position="fixed" sx={{ zIndex: theme.zIndex.drawer + 1 }}>
                <Toolbar>
                    <Typography variant="h6" style={{ flexGrow: 1 }}>
                        FlipVault
                    </Typography>
                    <Button color="inherit" component={Link} to="/login">Log in</Button>
                </Toolbar>
            </AppBar>
            <Drawer
                variant="permanent"
                sx={{
                    width: drawerWidth,
                    flexShrink: 0,
                    [`& .MuiDrawer-paper`]: { width: drawerWidth, boxSizing: 'border-box' },
                }}
            >
                <Toolbar />
                <Box sx={{ overflow: 'auto' }}>
                    <List>
                        <ListItem button component={Link} to="/user-dashboard" sx={{ color: 'text.primary' }}>
                            <ListItemIcon sx={{ color: 'text.primary' }}>
                                <DashboardIcon />
                            </ListItemIcon>
                            <ListItemText primary="Dashboard" />
                        </ListItem>
                        <ListItem button component={Link} to="/products" sx={{ color: 'text.primary' }}>
                            <ListItemIcon sx={{ color: 'text.primary' }}>
                                <ShoppingCartIcon />
                            </ListItemIcon>
                            <ListItemText primary="Products" />
                        </ListItem>
                        <ListItem button component={Link} to="/fee-calculator" sx={{ color: 'text.primary' }}>
                            <ListItemIcon sx={{ color: 'text.primary' }}>
                                <CalculateIcon />
                            </ListItemIcon>
                            <ListItemText primary="Fee Calculator" />
                        </ListItem>
                        <ListItem button component={Link} to="/settings" sx={{ color: 'text.primary' }}>
                            <ListItemIcon sx={{ color: 'text.primary' }}>
                                <SettingsIcon />
                            </ListItemIcon>
                            <ListItemText primary="Settings" />
                        </ListItem>
                        <ListItem button component={Link} to="/logout" sx={{ color: 'text.primary' }}>
                            <ListItemIcon sx={{ color: 'text.primary' }}>
                                <ExitToAppIcon />
                            </ListItemIcon>
                            <ListItemText primary="Log Out" />
                        </ListItem>
                    </List>
                </Box>
            </Drawer>
            <Box component="main" sx={{ flexGrow: 1, p: 3, ml: drawerWidth }}>
                <Toolbar />
                <Routes>
                    <Route path="/fee-calculator" element={<FeeCalculatorPage />} />
                    <Route path="/settings" element={<Settings />} />
                    <Route path="/logout" element={<Logout />} />
                    <Route path="/user-dashboard" element={<UserDashboard />} />
                </Routes>
                <Container maxWidth="sm" sx={{ mt: 2, backgroundColor: '#333', color: '#fff', padding: 4, borderRadius: 2 }}>
                    <Typography variant="h4" sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', mb: 2 }}>
                        {product.name}
                    </Typography>
                    <Paper sx={{ p: 2, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                        <img
                            src={product.image_url}
                            alt={product.name}
                            style={{ width: '100%', borderRadius: '4px' }}
                        />
                        <Typography variant="h6" sx={{ mt: 2 }}>
                            {product.name}
                        </Typography>
                    </Paper>
                </Container>
            </Box>
        </ThemeProvider>
    );
}

export default ProductCard;
