import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Typography, Paper, Box, AppBar, Toolbar, CssBaseline, Button, Drawer, List, ListItem, ListItemIcon, ListItemText, Tooltip, CircularProgress } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { Dashboard as DashboardIcon, ShoppingCart as ShoppingCartIcon, Settings as SettingsIcon, Calculate as CalculateIcon, ExitToApp as ExitToAppIcon } from '@mui/icons-material';
import { Link } from 'react-router-dom';

const theme = createTheme({
    palette: {
        mode: 'dark',
        primary: {
            main: '#90caf9',
        },
        secondary: {
            main: '#f48fb1',
        },
        background: {
            default: '#1c1c1c',
            paper: '#2c2c2c',
        },
        text: {
            primary: '#ffffff',
        },
    },
});

function ProductCard() {
    const { productId } = useParams();
    console.log('useParams:', useParams());
    const parsedProductId = productId && /^\d+$/.test(productId) ? parseInt(productId, 10) : NaN;
    const [product, setProduct] = useState(null);

    useEffect(() => {
        if (isNaN(parsedProductId)) {
            console.error('Product ID is not a valid integer:', productId);
            return;  // Stop execution if ID is invalid
        }

        fetch(`http://localhost:8000/products/${parsedProductId}`, {
            method: 'GET',
            credentials: 'include',
            headers: {
                'Authorization': 'Basic ' + btoa('juggy:Idus1234@@'),
                'Content-Type': 'application/json'
            },
        })
            .then(response => {
                if (response.status === 405) {
                    throw new Error('Method not allowed');
                }
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                console.log('API response:', data); // Log the API response
                setProduct({
                    ...data,
                    ...data.data, // Spread the `data` field into the product object
                });
            })
            .catch(error => console.error('Fetch error:', error));
    }, [parsedProductId]);

    const drawerWidth = 240;

    const SearchVolumeItem = ({ country, flagUrl, searchVolume }) => (
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
            <Tooltip title={country} arrow>
                <img src={flagUrl} alt={country} style={{ width: '24px', marginRight: '8px' }} />
            </Tooltip>
            <Typography variant="body1">{searchVolume ? `${searchVolume} Searches` : <CircularProgress size={20} />}</Typography>
        </Box>
    );

    const InfoItem = ({ title, value }) => (
        <Box sx={{ mb: 2 }}>
            <Typography variant="h6">{title}</Typography>
            <Typography variant="body1">
                {value !== undefined && value !== null ? value : (
                    <Box sx={{ display: 'flex', alignItems: 'center' }}>
                        <CircularProgress size={20} sx={{ mr: 1 }} />
                        Loading...
                    </Box>
                )}
            </Typography>
        </Box>
    );

    return (
        <ThemeProvider theme={theme}>
            <CssBaseline />
            <AppBar position="fixed" sx={{ zIndex: theme.zIndex.drawer + 1 }}>
                <Toolbar>
                    <Typography variant="h6" style={{ flexGrow: 1 }}>
                        FlipVault
                    </Typography>
                    <Button color="inherit" component={Link} to="/login">Log in</Button>
                </Toolbar>
            </AppBar>
            <Drawer
                variant="permanent"
                sx={{
                    width: drawerWidth,
                    flexShrink: 0,
                    [`& .MuiDrawer-paper`]: { width: drawerWidth, boxSizing: 'border-box' },
                }}
            >
                <Toolbar />
                <Box sx={{ overflow: 'auto' }}>
                    <List>
                        <ListItem button component={Link} to="/user-dashboard" sx={{ color: 'text.primary' }}>
                            <ListItemIcon sx={{ color: 'text.primary' }}>
                                <DashboardIcon />
                            </ListItemIcon>
                            <ListItemText primary="Dashboard" />
                        </ListItem>
                        <ListItem button component={Link} to="/products" sx={{ color: 'text.primary' }}>
                            <ListItemIcon sx={{ color: 'text.primary' }}>
                                <ShoppingCartIcon />
                            </ListItemIcon>
                            <ListItemText primary="Products" />
                        </ListItem>
                        <ListItem button component={Link} to="/fee-calculator" sx={{ color: 'text.primary' }}>
                            <ListItemIcon sx={{ color: 'text.primary' }}>
                                <CalculateIcon />
                            </ListItemIcon>
                            <ListItemText primary="Fee Calculator" />
                        </ListItem>
                        <ListItem button component={Link} to="/settings" sx={{ color: 'text.primary' }}>
                            <ListItemIcon sx={{ color: 'text.primary' }}>
                                <SettingsIcon />
                            </ListItemIcon>
                            <ListItemText primary="Settings" />
                        </ListItem>
                        <ListItem button component={Link} to="/logout" sx={{ color: 'text.primary' }}>
                            <ListItemIcon sx={{ color: 'text.primary' }}>
                                <ExitToAppIcon />
                            </ListItemIcon>
                            <ListItemText primary="Log Out" />
                        </ListItem>
                    </List>
                </Box>
            </Drawer>
            <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
                <Toolbar />
                <Container maxWidth="sm" sx={{ mt: 2, backgroundColor: '#333', color: '#fff', padding: 4, borderRadius: 2 }}>
                    <Typography variant="h4" sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', mb: 2 }}>
                        {product?.name ? (
                            product.name
                        ) : (
                            <Box sx={{ display: 'flex', alignItems: 'center' }}>
                                <CircularProgress size={24} sx={{ mr: 1 }} />
                            </Box>
                        )}
                    </Typography>
                    <Paper sx={{ p: 2, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                        {product?.image_url ? (
                            <img src={product.image_url} alt={product.name || 'Product'} style={{ width: '100%', borderRadius: '4px' }} />
                        ) : (
                            <React.Fragment>
                                <CircularProgress />
                            </React.Fragment>
                        )}
                    </Paper>
                </Container>

                <Container maxWidth="lg" sx={{ mt: 4, display: 'flex', justifyContent: 'space-between' }}>
                    <Box sx={{ backgroundColor: '#333', color: '#fff', padding: 4, borderRadius: 2, flex: 1, mr: 2 }}>
                        <Typography variant="h5" sx={{ mb: 2 }}>
                            Product Analytics
                        </Typography>
                        <Paper sx={{ p: 2, mb: 2 }}>
                            <Typography variant="h6">Search Volume</Typography>
                            <SearchVolumeItem
                                country="Australia"
                                flagUrl="https://upload.wikimedia.org/wikipedia/en/b/b9/Flag_of_Australia.svg"
                                searchVolume={product?.search_volume_au}
                            />
                            <SearchVolumeItem
                                country="United States"
                                flagUrl="https://upload.wikimedia.org/wikipedia/en/a/a4/Flag_of_the_United_States.svg"
                                searchVolume={product?.search_volume_us}
                            />
                            <SearchVolumeItem
                                country="United Kingdom"
                                flagUrl="https://upload.wikimedia.org/wikipedia/en/a/ae/Flag_of_the_United_Kingdom.svg"
                                searchVolume={product?.search_volume_uk}
                            />
                        </Paper>
                        <Paper sx={{ p: 2, mb: 2 }}>
                            <Typography variant="h6">Trend Over Time</Typography>
                            {/* Add a line chart component for each country */}
                            {/*<LineChart data={product?.search_volume_trend_au} />
                            <LineChart data={product?.search_volume_trend_us} />
                            <LineChart data={product?.search_volume_trend_uk} />*/}
                        </Paper>
                        <Paper sx={{ p: 2, mb: 2 }}>
                            <Typography variant="h6">Top Keywords</Typography>
                            {/*<KeywordList keywords={product?.top_keywords_au} />
                            <KeywordList keywords={product?.top_keywords_us} />
                            <KeywordList keywords={product?.top_keywords_uk} />*/}
                        </Paper>
                    </Box>

                    <Box sx={{ backgroundColor: '#333', color: '#fff', padding: 4, borderRadius: 2, flex: 1, ml: 2 }}>
                        <Typography variant="h5" sx={{ mb: 2 }}>
                            Pricing and Listings
                        </Typography>
                        <Paper sx={{ p: 2, mb: 2 }}>
                            <Typography variant="h6">Average Pricing</Typography>
                            <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '10px' }}>
      <InfoItem title="Depop" value={product ? `$${product.average_price_depop}` : null} />
      <InfoItem title="Ebay" value={product ? `$${product.average_price}` : null} />
      <InfoItem title="Facebook Marketplace" value={product ? `$${product.average_price_facebook}` : null} />
    </div>
                        </Paper>
                        <Paper sx={{ p: 2 }}>
                            <InfoItem title="Listing Amount" value={product ? product.listings : null} />
                            <InfoItem title="Total Sold" value={product ? `$${product.sale_amount} Over the last 60 sales` : null} />
                        </Paper>
                    </Box>
                </Container>
            </Box>
        </ThemeProvider>
    );
}

export default ProductCard;
