import React, { useState } from 'react';
import { Container, Typography, TextField, Button, Box, Select, MenuItem } from '@mui/material';
import axios from 'axios';

function FeeCalculatorPage() {
  const [salePrice, setSalePrice] = useState('');
  const [marketplace, setMarketplace] = useState('');
  const [fee, setFee] = useState(null);
  const [error, setError] = useState(null);

  const handleCalculateFee = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/calculate_fee', { // Update the URL
        sale_price: parseFloat(salePrice),
        marketplace: marketplace,
      });
      setFee(response.data.fee);
      setError(null);
    } catch (err) {
      if (err.response && err.response.data) {
        setError(err.response.data.error);
      } else {
        setError('An unexpected error occurred');
      }
      setFee(null);
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h3" gutterBottom>
        Fee Calculator
      </Typography>
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
        <TextField
          label="Sale Price"
          variant="outlined"
          type="number"
          value={salePrice}
          onChange={(e) => {
            const value = parseFloat(e.target.value);
            if (value <= 10000) {
              setSalePrice(e.target.value);
            }
          }}
          inputProps={{ min: 0, max: 10000 }}
        />
        <Select
          label="Marketplace"
          variant="outlined"
          value={marketplace}
          onChange={(e) => setMarketplace(e.target.value)}
        >
          <MenuItem value="Depop">Depop</MenuItem>
          <MenuItem value="Ebay">Ebay</MenuItem>
          <MenuItem value="Mercari">Mercari</MenuItem>
          <MenuItem value="Offerup">Offerup</MenuItem>
          <MenuItem value="Poshmark">Poshmark</MenuItem>
          <MenuItem value="StockX">StockX</MenuItem>
        </Select>
        <Button variant="contained" color="primary" onClick={handleCalculateFee}>
          Calculate Fee
        </Button>
        {fee !== null && (
          <Typography variant="h6">
            The fee for selling on {marketplace} is ${typeof fee === 'number' ? fee.toFixed(2) : 'N/A'}
          </Typography>
        )}
        {error && (
          <Typography variant="h6" color="error">
            An error occurred, Try again later
          </Typography>
        )}
      </Box>
    </Container>
  );
}

export default FeeCalculatorPage;
