import React, { useState } from 'react';

const ProductForm = ({ onCreate }) => {
  const [name, setName] = useState('');
  const [imageUrl, setImageUrl] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (name === '' || imageUrl === '') {
      alert('Please fill in all fields');
      return;
    }
    onCreate({ name, imageUrl }); // Ensure key names match backend schema
    setName('');
    setImageUrl('');
  };

  return (
    <form onSubmit={handleSubmit} style={formStyle}>
      <h2 style={headerStyle}>Add Product</h2>
      <div style={formGroupStyle}>
        <label htmlFor="name" style={labelStyle}>Name:</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={inputStyle}
          required
        />
      </div>
      <div style={formGroupStyle}>
        <label htmlFor="imageUrl" style={labelStyle}>Image URL:</label>
        <input
          type="text"
          id="imageUrl"
          value={imageUrl}
          onChange={(e) => setImageUrl(e.target.value)}
          style={inputStyle}
          required
        />
      </div>
      <button type="submit" style={buttonStyle}>Add Product</button>
    </form>
  );
};

export default ProductForm;
