import React from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, Typography } from '@mui/material';
import { useState } from 'react';
import { Box, Dialog, DialogActions, DialogContent, DialogTitle, TextField } from '@mui/material';
import { scrapeProduct, storeSearchVolume } from '../services/api';

const ProductList = ({ products, onDelete, onScrape, onStoreVolume }) => {
    const [open, setOpen] = useState(false);
    const [selectedProductId, setSelectedProductId] = useState(null);
    const [volumeData, setVolumeData] = useState({
      search_volume_us: '',
      search_volume_au: '',
      search_volume_uk: '',
    });
  
    const handleOpen = (id) => {
      setSelectedProductId(id);
      setOpen(true);
    };
  
    const handleClose = () => {
      setOpen(false);
      setSelectedProductId(null);
      setVolumeData({ search_volume_us: '', search_volume_au: '', search_volume_uk: '' });
    };
  
    const handleChange = (e) => {
      setVolumeData({ ...volumeData, [e.target.name]: e.target.value });
    };
  
    const handleSubmit = () => {
      if (selectedProductId) {
        onStoreVolume(selectedProductId, volumeData);
      }
      handleClose();
    };
  
    return (
      <Box>
        {products.map((product) => (
          <Box key={product.id} mb={2} border="1px solid #ccc" borderRadius={2} p={2}>
            <Typography variant="h6">{product.name}</Typography>
            <Button onClick={() => onDelete(product.id)} color="error">Delete</Button>
            <Button onClick={() => onScrape(product.id)} sx={{ ml: 1 }}>Scrape</Button>
            <Button onClick={() => handleOpen(product.id)} sx={{ ml: 1 }} color="secondary">Set Search Volume</Button>
          </Box>
        ))}
  
        <Dialog open={open} onClose={handleClose}>
          <DialogTitle>Enter Search Volume</DialogTitle>
          <DialogContent>
            <TextField
              margin="dense"
              name="search_volume_us"
              label="US Search Volume"
              fullWidth
              value={volumeData.search_volume_us}
              onChange={handleChange}
            />
            <TextField
              margin="dense"
              name="search_volume_au"
              label="AU Search Volume"
              fullWidth
              value={volumeData.search_volume_au}
              onChange={handleChange}
            />
            <TextField
              margin="dense"
              name="search_volume_uk"
              label="UK Search Volume"
              fullWidth
              value={volumeData.search_volume_uk}
              onChange={handleChange}
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose}>Cancel</Button>
            <Button onClick={handleSubmit} variant="contained" color="primary">Save</Button>
          </DialogActions>
        </Dialog>
      </Box>
    );
};

export default ProductList;
