import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000';

export const login = async (username, password) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/login`, {}, {
      auth: {
        username,
        password,
      },
    });
    return response.status === 200;
  } catch (error) {
    console.error('Login error', error);
    return false;
  }
};

export const getProducts = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/products`);
    return response.data;
  } catch (error) {
    console.error('Error fetching products', error);
    return [];
  }
};

export const createProduct = async (product) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/products/`, product, {
      auth: {
        username: 'juggy', // Replace with actual username
        password: 'Idus1234@@', // Replace with actual password
      },
      headers: {
        'Content-Type': 'application/json',
      },
    });
    return response.data;
  } catch (error) {
    console.error('Error creating product', error);
    return null;
  }
};

export const deleteProduct = async (productId) => {
  try {
    await axios.delete(`${API_BASE_URL}/products/${productId}/delete`, {
      auth: {
        username: 'juggy', // Replace with actual username
        password: 'Idus1234@@', // Replace with actual password
      },
    });
  } catch (error) {
    console.error('Error deleting product', error);
  }
};

export const scrapeProducts = async () => {
    try {
      const response = await axios.post(`${API_BASE_URL}/products/scrape`, {}, {
        auth: {
            username: 'juggy', // Replace with actual username
            password: 'Idus1234@@', // Replace with actual password
        },
        headers: {
          'Content-Type': 'application/json',
        },
      });
      return response.data;
    } catch (error) {
      console.error('Error starting scraper', error);
      return null;
    }
};

