import React from 'react';
import { Container, Typography, Button, AppBar, Toolbar, IconButton, Box, CssBaseline, Menu, MenuItem } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { Menu as MenuIcon } from '@mui/icons-material';
import { Link } from 'react-router-dom';
import { styled } from '@mui/system';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#64b5f6', // A slightly more modern blue
    },
    secondary: {
      main: '#f48fb1',
    },
    background: {
      default: '#121212', // Darker background
      paper: '#1e1e1e', // Darker paper
    },
    text: {
      primary: '#e0e0e0', // Lighter text
    },
  },
  typography: {
    fontFamily: 'Roboto, sans-serif', // Use a more modern font
    h2: {
      fontWeight: 700,
      fontSize: '3rem',
    },
    h5: {
      fontWeight: 400,
      fontSize: '1.5rem',
    },
  },
});

// Styled Button Component
const ModernButton = styled(Button)({
  borderRadius: '8px',
  padding: '12px 24px',
  fontSize: '1rem',
  fontWeight: 600,
  boxShadow: 'none',
  textTransform: 'none',
  '&:hover': {
    boxShadow: '0px 3px 5px rgba(0,0,0,0.2)',
  },
});

function LandingPage() {
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AppBar position="static">
        <Toolbar>
          <IconButton edge="start" color="inherit" aria-label="menu" onClick={handleMenuOpen}>
            <MenuIcon />
          </IconButton>
          <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={handleMenuClose}>
            <MenuItem component={Link} to="/" onClick={handleMenuClose}>Home</MenuItem>
            <MenuItem component={Link} to="/showcase" onClick={handleMenuClose}>Showcase</MenuItem>
            <MenuItem component={Link} to="/pricing" onClick={handleMenuClose}>Pricing</MenuItem>
          </Menu>
          <Typography variant="h6" style={{ flexGrow: 1 }}>
            FlipVault
          </Typography>
          <Button color="inherit" component={Link} to="/login">Log in</Button>
        </Toolbar>
      </AppBar>
      <Container maxWidth="lg" style={{ marginTop: '40px', textAlign: 'center' }}>
        <Typography variant="h2" component="h1" gutterBottom>
          The Exclusive Platform for Reselling
        </Typography>
        <Typography variant="h5" color="textSecondary" gutterBottom>
          Payments through Stripe
        </Typography>
        <Box mt={4}>
          <iframe
            width="560"
            height="315"
            src="https://www.youtube.com/embed/KuZBwUZrVvA"
            title="YouTube video player"
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
            style={{ maxWidth: '100%', marginBottom: '30px', borderRadius: '12px' }} // Added border-radius
          ></iframe>
        </Box>
        <ModernButton variant="contained" color="primary" size="large" Component={Link} to="/login" style={{ borderRadius: '8px' }}>
          GET ACCESS
        </ModernButton>
      </Container>
    </ThemeProvider>
  );
}

export default LandingPage;
