import React, { useState } from 'react';
import { TextField, Button, Typography, Paper, Box } from '@mui/material';

const ProductForm = ({ onCreate }) => {
  const [name, setName] = useState('');
  const [imageUrl, setImageUrl] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (name === '' || imageUrl === '') {
      alert('Please fill in all fields');
      return;
    }
    onCreate({ name, imageUrl }); // Ensure key names match backend schema
    setName('');
    setImageUrl('');
  };

  return (
    <Paper component="form" onSubmit={handleSubmit} style={formStyle}>
      <Typography variant="h4" component="h2" style={headerStyle}>
        Add Product
      </Typography>
      <Box style={formGroupStyle}>
        <TextField
          label="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={inputStyle}
          variant="outlined"
          fullWidth
          required
        />
      </Box>
      <Box style={formGroupStyle}>
        <TextField
          label="Image URL"
          value={imageUrl}
          onChange={(e) => setImageUrl(e.target.value)}
          style={inputStyle}
          variant="outlined"
          fullWidth
          required
        />
      </Box>
      <Button type="submit" variant="contained" color="primary" style={buttonStyle}>
        Add Product
      </Button>
    </Paper>
  );
};

const formStyle = {
  maxWidth: '400px',
  margin: '0 auto',
  padding: '1rem',
  border: '1px solid #ccc',
  borderRadius: '8px',
  backgroundColor: '#f9f9f9',
};

const headerStyle = {
  textAlign: 'center',
  marginBottom: '1rem',
};

const formGroupStyle = {
  marginBottom: '1rem',
};

const labelStyle = {
  display: 'block',
  marginBottom: '0.5rem',
  fontWeight: 'bold',
};

const inputStyle = {
  width: '100%',
  padding: '0.5rem',
  borderRadius: '4px',
  border: '1px solid #ccc',
};

export default ProductForm;
